mapper.select:
SELECT b.id id, b.url url, b.`name` `name`, b.parent_id parent_id, b.type type FROM user_permission_relation a LEFT JOIN permissions b ON a.permission_id = b.id WHERE a.user_id = #{userId}
AND b.type = #{type}
AND b.`status` = 1 AND b.id IS NOT NULL ORDER BY b.sort

SELECT b.id id, b.url url, b.`name` `name`, b.parent_id parent_id, b.type type FROM user_permission_relation a LEFT JOIN permissions b ON a.permission_id = b.id LEFT JOIN users c ON a.user_id = c.id WHERE c.username = #{username}
AND b.type = #{type}
AND b.`status` = 1 and c.`status` = 1 AND b.id IS NOT NULL AND c.id IS NOT NULL ORDER BY b.sort

SELECT a.user_id, a.permission_id, b.name, b.type
from user_permission_relation a
         LEFT JOIN permissions b on a.permission_id = b.id and b.STATUS = 1;

SELECT a.user_id, a.model_id, b.name
from user_model_relation a
         LEFT JOIN device_models b on a.model_id = b.id;
